i1.b:
	//input count and type check:
	var customScripts = getAPI().getCustomScripts();
	if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
		{   typeof(string), typeof(List<Dictionary<IFResource, Tuple<double, double>>>)   }
	  }, out _))
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_mult_resource custom script received invalid arguments");
		return null;
	}

	string inMarkup = (string)inArguments[0];
	List<Dictionary<IFResource, Tuple<double, double>>> inListsOfResources = (List<Dictionary<IFResource, Tuple<double, double>>>)inArguments[1];

	var consts = getAPI().getConsts();
	var customConsts = getAPI().getCustomConsts();
	bool success = false;

	string outDescription = "";

	//before anything else, check if the |uniquify option is set:
	bool uniquifyIDs = false;
	if(inMarkup.Contains("|uniquify"))
	{
		uniquifyIDs = true;
		inMarkup = inMarkup.Replace("|uniquify", "");
	}

	ReadOnlyCollection<string> possibleOptions = customConsts.getListConst("replaceDescriptionMarkupKind_mult_resource list_of_options", out success);
	if(!success)
	{
		addDebugMessage(inScriptInstance, "list of options could not be found");
		return new List<object>() { outDescription };
	}

	string default_option = "name_noun_list";

	//find the string between the | and : characters:
	string optionString = default_option;
	int optionStartLoc = inMarkup.IndexOf("|");
	int optionEndLoc = inMarkup.IndexOf(":");
	if((optionStartLoc != -1) && (optionEndLoc != -1))
	{
		string toCheck = inMarkup.Substring(optionStartLoc + 1, optionEndLoc - (optionStartLoc + 1));
		if(possibleOptions.Contains(toCheck.ToLower()))
		{
			optionString = toCheck.ToLower();
		}
	}

	//gather the resources from the indicated numbers:
	bool error = false;
	List<IFResource> resources = new List<IFResource>();
	string ids = inMarkup.Substring(optionEndLoc + 1);
	ids = ids.Substring(0, ids.Length - 1);
	foreach(string entry in ids.Split(','))
	{
		if(Int64.TryParse(entry, out Int64 idAsInt))
		{
			IFResource resource = (IFResource)getAPI().getMOFromMOID("resources", idAsInt);
			if(resource == null)
			{
				error = true;
				addDebugMessage(inScriptInstance, "markup indicated invalid MOID of resource, MOID=" + idAsInt);
				break;
			}
			resources.Add(resource);
		}
		else
		{
			error = true;
			addDebugMessage(inScriptInstance, "unable to parse markup for id; id string=\"" + entry + "\" and markup=" + inMarkup);
			break;
		}
	}

	if(!error)
	{
		if(customScripts.doesCustomScriptExistWithName("replaceDescriptionMarkupKind_mult_resource" + "_" + optionString))
		{
			if(customScripts.runCustomScriptCheckTypes("replaceDescriptionMarkupKind_mult_resource" + "_" + optionString, new List<object>() { resources }, out List<object> replacedMarkup, false, typeof(string)))
			{
				outDescription = (string)replacedMarkup[0];
			}
			else
			{
				addDebugMessage(inScriptInstance, "markup kind script replaceDescriptionMarkupKind_mult_resource" + "_" + optionString + " did not execute correctly; something went wrong; terminating replacement");
				error = true;
			}
		}
		else
		{
			addDebugMessage(inScriptInstance, "markup kind script replaceDescriptionMarkupKind_mult_resource" + "_" + optionString + " does not exist despite markup kind and option being valid; something went wrong; terminating replacement");
			error = true;
		}
	}

	if(error)
	{
		string errorReplacer = customConsts.getStringConst("replaceDescriptionMarkupKind_mult_resource invalid_resource_error before_replace_text", out success);
		if(!success) errorReplacer = "INVALID_RESOURCES";
		outDescription = errorReplacer;
	}

    return new List<object>() { outDescription };
