i1.b:
	//input count and type check:
	var customScripts = getAPI().getCustomScripts();
	if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
		{   typeof(IFBuildingKind)   }
	  }, out _))
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_buildingkind_shapeside_noun_phrase custom script received invalid arguments");
		return null;
	}

	IFBuildingKind inBuildingKind = (IFBuildingKind)inArguments[0];

	var consts = getAPI().getConsts();
	var customConsts = getAPI().getCustomConsts();
	bool success = false;

	string outDescription = "";

	if(inBuildingKind == null)
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_buildingkind_shapeside_noun_phrase custom script received invalid arguments: inBuildingKind cannot be null");
		return null;
	}

	List<string> descriptionNouns = new List<string>();
	foreach(string tag in inBuildingKind.getTagList())
	{
		if(tag.Contains("description_shape_side_noun:"))
		{
			descriptionNouns.Add(tag.Replace("description_shape_side_noun:", ""));
		}
	}

	Int64 randCountTotal = customConsts.getIntConst("replaceDescriptionMarkupKind_one_buildingkind_shapeside_noun_phrase rand_selection_count", out success);
	if(!success) randCountTotal = 1;
	List<string> possibleSelections = new List<string>();

	for(int i = 0; i < randCountTotal; i++)
	{
		string randSel = customConsts.getStringConst("replaceDescriptionMarkupKind_one_buildingkind_shapeside_noun_phrase rand_sel" + i + " before_replace_text", out success);
		if(!success) continue;

		possibleSelections.Add(randSel);
	}

	if(possibleSelections.Count > 0)
	{
		outDescription = possibleSelections[(int)getAPI().calcRandIntUnder(possibleSelections.Count)];
	}
	else
	{
		outDescription = customConsts.getStringConst("replaceDescriptionMarkupKind_one_buildingkind_shapeside_noun_phrase default before_replace_text", out success);
		if(!success) outDescription = "[0]";
	}

	if(descriptionNouns.Count > 0)
	{
		outDescription = outDescription.Replace("[0]", descriptionNouns[(int)getAPI().calcRandIntUnder(descriptionNouns.Count)]);
	}
	else
	{
		outDescription = "";
	}

    return new List<object>() { outDescription };
