i1.b:
	//input count and type check:
	var customScripts = getAPI().getCustomScripts();
	if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
		{   typeof(IFDelegation)   }
	  }, out _))
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_delegation_name_noun_phrase custom script received invalid arguments");
		return null;
	}

	IFDelegation inDelegation = (IFDelegation)inArguments[0];

	var consts = getAPI().getConsts();
	var customConsts = getAPI().getCustomConsts();
	bool success = false;

	if(inDelegation == null)
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_delegation_name_noun_phrase custom script received invalid arguments: inDelegation cannot be null");
		return null;
	}

	string outDescription = "";

	string displayName = inDelegation.getDisplayName();
	string descriptionAdj = "";
	if(customScripts.doesCustomScriptExistWithName("replaceDescriptionMarkupKind_one_delegation_rand_adj_phrase"))
	{
		if(customScripts.runCustomScriptCheckTypes("replaceDescriptionMarkupKind_one_delegation_rand_adj_phrase", new List<object>() { inDelegation }, out List<object> randAdj, false, typeof(string)))
		{
			descriptionAdj = (string)randAdj[0];
		}
	}

	Int64 randCountTotal = customConsts.getIntConst("replaceDescriptionMarkupKind_one_delegation_name_noun_phrase rand_selection_count", out success);
	if(!success) randCountTotal = 6;
	List<string> possibleSelections = new List<string>();

	for(int i = 0; i < randCountTotal; i++)
	{
		string randSel = customConsts.getStringConst("replaceDescriptionMarkupKind_one_delegation_name_noun_phrase rand_sel" + i + " before_replace_text", out success);
		if(!success) continue;

		if(randSel.Contains("[1]") && descriptionAdj == "")
			continue;

		possibleSelections.Add(randSel);
	}

	if(possibleSelections.Count > 0)
	{
		outDescription = possibleSelections[(int)getAPI().calcRandIntUnder(possibleSelections.Count)];
	}
	else
	{
		outDescription = customConsts.getStringConst("replaceDescriptionMarkupKind_one_delegation_name_noun_phrase default before_replace_text", out success);
		if(!success) outDescription = "[0]";
	}

	outDescription = outDescription.Replace("[0]", displayName);
	outDescription = outDescription.Replace("[1]", descriptionAdj);

    return new List<object>() { outDescription };
