	}

	string outDescription = "";

	string possiblePhrases = customConsts.getStringConst("replaceDescriptionMarkupKind_one_delegation_rand_adj_phrase other_adj_phrase_names list", out success);
	List<string> possibles = new List<string>(possiblePhrases.Split(','));
	if(possibles.Count != 0)
