		string phraseChosen = possibles[(int)getAPI().calcRandIntUnder(possibles.Count)];

		if(customScripts.doesCustomScriptExistWithName("replaceDescriptionMarkupKind_one_delegation_" + phraseChosen + "_adj_phrase"))
