i1.b:
	//input count and type check:
	var customScripts = getAPI().getCustomScripts();
	if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
		{   typeof(IFPop)   }
	  }, out _))
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_pop_target_3ppronoun_phrase custom script received invalid arguments");
		return null;
	}

	IFPop inPop = (IFPop)inArguments[0];

	var consts = getAPI().getConsts();
	var customConsts = getAPI().getCustomConsts();
	bool success = false;

	if(inPop == null)
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_pop_target_3ppronoun_phrase custom script received invalid arguments: inPop cannot be null");
		return null;
	}

	string outDescription = "";

	//determine the sex of the pop:
	switch(inPop.getSex())
	{
		default:
		case IFPop.ePopSex.cUnisexNone:
			outDescription = customConsts.getStringConst("replaceDescriptionMarkupKind_one_pop_target_3ppronoun_phrase unisex_none", out success);
			if(!success) outDescription = "them";
		break;

		case IFPop.ePopSex.cUnisexBoth:
			outDescription = customConsts.getStringConst("replaceDescriptionMarkupKind_one_pop_target_3ppronoun_phrase unisex_both", out success);
			if(!success) outDescription = "them";
		break;

		case IFPop.ePopSex.cMale:
			outDescription = customConsts.getStringConst("replaceDescriptionMarkupKind_one_pop_target_3ppronoun_phrase unisex_male", out success);
			if(!success) outDescription = "him";
		break;

		case IFPop.ePopSex.cFemale:
			outDescription = customConsts.getStringConst("replaceDescriptionMarkupKind_one_pop_target_3ppronoun_phrase unisex_female", out success);
			if(!success) outDescription = "her";
		break;
	}

    return new List<object>() { outDescription };
