i1.b:
	//input count and type check:
	var customScripts = getAPI().getCustomScripts();
	if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
		{   typeof(IFRace)   }
	  }, out _))
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_race_race_nonname_adj_phrase custom script received invalid arguments");
		return null;
	}

	IFRace inRace = (IFRace)inArguments[0];

	var consts = getAPI().getConsts();
	var customConsts = getAPI().getCustomConsts();
	bool success = false;

	if(inRace == null)
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_race_race_nonname_adj_phrase custom script received invalid arguments: inRace cannot be null");
		return null;
	}

	string outDescription = "";

	List<string> possibleAdjs = new List<string>();
	foreach(string tag in inRace.getTagList())
	{
		if(tag.Contains("general_adj:"))
		{
			possibleAdjs.Add(tag.Replace("general_adj:", "").ToLower());
		}
	}

	if(possibleAdjs.Count > 0)
	{
		outDescription = possibleAdjs[(int)getAPI().calcRandIntUnder(possibleAdjs.Count)];
	}

    return new List<object>() { outDescription };
