i1.b:
	//input count and type check:
	var customScripts = getAPI().getCustomScripts();
	if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
		{   typeof(IFResourcePool)   }
	  }, out _))
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_resourcepool_no_qualities_list custom script received invalid arguments");
		return null;
	}

	IFResourcePool inResourcePool = (IFResourcePool)inArguments[0];

	var consts = getAPI().getConsts();
	var customConsts = getAPI().getCustomConsts();
	bool success = false;

	string outDescription = "";

	if(inResourcePool == null)
	{
		throw new ArgumentException("replaceDescriptionMarkupKind_one_resourcepool_no_qualities_list custom script received invalid arguments: inResourcePool cannot be null");
		return null;
	}

	//assemble the Dictionary of resource->tuple of quan/qual so that the common script can take care of it:
	Dictionary<IFResource, Tuple<double, double>> dictOfResources = new Dictionary<IFResource, Tuple<double, double>>();
	for(int moid = 1; moid < getAPI().getTotalCountOfMO("resources"); moid++)
	{
		IFResource res = (IFResource)getAPI().getMOFromMOID("resources", moid);
		if(res != null)
		{
			double quan = inResourcePool.getResourceQuantity(res);
			if(quan != 0.0d)
			{
				dictOfResources.Add(res, Tuple.Create(quan, inResourcePool.getResourceQuality(res)));
			}
		}
	}

	if(customScripts.runCustomScriptCheckTypes("replaceDescriptionMarkupKind_common_resourcedictionary", new List<object>() { dictOfResources, false }, out List<object> outList, false, typeof(string)))
	{
		outDescription = (string)outList[0];
	}
	else
	{
		addDebugMessage(inScriptInstance, "replaceDescriptionMarkupKind_common_resourcedictionary failed to execute; resource pool will not be displayed");
	}

    return new List<object>() { outDescription };
