//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_Dead_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_Dead_hasValidTargetForTransitionIn is checking if target exists for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//only consideration: is the pop dead
toReturn.Add(inPop.isDead());

addDebugMessage(inScriptInstance, "return value=" + (bool)toReturn[0]);
return toReturn;

//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_Dead_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_Dead_findTargetForTransitionIn finding target for pop " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//if a pop has BeingAbducted variables stored, clear them here:
Int64 uidOfAbductor = variableHandler.getInt("battleAI_UIDOfAbductorOf_pop" + inPop.getUID(), out bool success);
if(success)
{
	variableHandler.storeIntVariable(getAPI().getCalendar().getCurrentDate(), "battleAI_UIDOfAbductorOf_pop" + inPop.getUID(), Int64.MinValue);
	IFPop pop = (IFPop)getAPI().getSO("pops", uidOfAbductor);
	if(pop != null)
	{
		variableHandler.storeIntVariable(getAPI().getCalendar().getCurrentDate(), "battleAI_UIDOfPopAbductedBy_pop" + uidOfAbductor, Int64.MinValue);
	}
}
//if the pop is stealing or abducting a pop, stop that:
customScripts.runCustomScript("battleAIDropLootAndAbductee", new List<object>() {inPop}, out _);

//this is simple: just tell the pop to idle; it's dead
toReturn.Add(null);
toReturn.Add(null);
toReturn.Add(false);
toReturn.Add(-1.0d);
toReturn.Add(-1.0d);
return toReturn;

