i1.b:
//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_EnemyFleeingMapSuccess_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_EnemyFleeingMapSuccess_hasValidTargetForTransitionIn is checking if target exists for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//three considerations:
//the first consideration is if the pop is an enemy pop during battle:
bool first_consideration = getAPI().isHostileCommunity(inPop.getCommunity()) && inBattleOngoing;

//the second consideration is if the community of the enemy pop has reached their wargoal
Int64 reached_wargoal = variableHandler.getInt("battleAI_reachedBattleGoal_community" + inPop.getCommunity().getUID(), out bool success);
if(!success) reached_wargoal = 0;
bool second_consideration = (reached_wargoal != 0);

//however, there is a third consideration, which will override the second consideration if true: if it has been determined that this enemy has no possible targets, it should leave the map:
Int64 noPossibleTargets = variableHandler.getInt("battleAI_leaveBattleEarlyNoTargets_pop" + inPop.getUID(), out success);
bool third_consideration = (success && (noPossibleTargets != 0));

toReturn.Add(first_consideration && (second_consideration || third_consideration));

addDebugMessage(inScriptInstance, "return value=" + (bool)toReturn[0]);
return toReturn;

//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_EnemyFleeingMapSuccess_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_EnemyFleeingMapSuccess_findTargetForTransitionIn finding target for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//this is simple: just tell the pop to leave the map
toReturn.Add(null);
toReturn.Add(null);
toReturn.Add(true);
toReturn.Add(-1.0d);
toReturn.Add(-1.0d);
return toReturn;

i1.a:
//input count check:
if(inArguments.Length < 3)
{
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();
var customScripts = getAPI().getCustomScripts();

addDebugMessage(inScriptInstance, "default state_EnemyFleeingMapSuccess script is finding target for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//if we are close enough to a map edge that we can leave the map, do so:
bool close_to_map_edge = false;
double x_loc = inPop.getMapLocationX();
double y_loc = inPop.getMapLocationY();
double radiusToLeaveMap = customConsts.getDoubleConst("pathfinding_state_EnemyFleeingMapSuccess leave_map radius", out bool success);
if(!success) radiusToLeaveMap = 0.25d;
if((x_loc <= radiusToLeaveMap) || (y_loc <= radiusToLeaveMap))
{
	close_to_map_edge = true;
}
else if((inLocalMap.getMapDimensions().Item1 <= (x_loc + radiusToLeaveMap)) || (inLocalMap.getMapDimensions().Item2 <= (y_loc + radiusToLeaveMap)))
{
	close_to_map_edge = true;
}
if(close_to_map_edge)
{
	inPop.removePopFromMap(IFPop.ePopLocation.cUnspecifiedNowhere);
	addDebugMessage(inScriptInstance, "pop has escaped the battle; removing from map (pathfinding scripts will return information for Idle state)");
	toReturn.Add("Idle");
	toReturn.Add(null);
	toReturn.Add(null);
	toReturn.Add(false);
	toReturn.Add(-1.0d);
	toReturn.Add(-1.0d);
	return toReturn;
}

//if the battle goal has been lost, now start to leave the map with a broken mindset (broken pops don't look to fight)
if(customScripts.runCustomScriptCheckTypes("attemptPathfindStateTransition", new List<object>() {"EnemyFleeingMapBroken", inPop, inLocalMap, inBattleOngoing},
	out List<object> validTgt2, true, typeof(bool), typeof(IFBuilding), typeof(IFPop), typeof(bool), typeof(double), typeof(double)))
{
	if((bool)validTgt2[0])
	{
		addDebugMessage(inScriptInstance, "custom scripts indicated pop can and should transition to the EnemyFleeingMapBroken state due to: " + " wargoal is now abandoned");
		toReturn.Add("EnemyFleeingMapBroken");
		toReturn.Add((IFBuilding)validTgt2[1]);
		toReturn.Add((IFPop)validTgt2[2]);
		toReturn.Add((bool)validTgt2[3]);
		toReturn.Add((double)validTgt2[4]);
		toReturn.Add((double)validTgt2[5]);
		return toReturn;
	}
}			
else
{
	//we failed to call a script correctly somehow.  Transition to Idle instead:
	addDebugMessage(inScriptInstance, "custom scripts indicated pop could not transition to the EnemyFleeingMapBroken state so pop is transitioning directly to Idle state");
	toReturn.Add("Idle");
	toReturn.Add(null);
	toReturn.Add(null);
	toReturn.Add(false);
	toReturn.Add(-1.0d);
	toReturn.Add(-1.0d);
	return toReturn;
}

//if there is an ongoing fight within the fighting radius, and it can be joined, join it:
if(customScripts.runCustomScriptCheckTypes("joinNearbyFightIfAble", new List<object>() {inPop},
	out List<object> validFight, true, typeof(bool)))
{
	if((bool)validFight[0])
	{
		addDebugMessage(inScriptInstance, "custom scripts indicated pop can and should transition to the Fighting state due to: " + " pop joined fight nearby");
		toReturn.Add("Fighting");
		toReturn.Add(null);
		toReturn.Add(null);
		toReturn.Add(false);
		toReturn.Add(-1.0d);
		toReturn.Add(-1.0d);
		return toReturn;
	}
}
else
{
	//we failed to call a script correctly somehow.  Transition to Idle instead:
	addDebugMessage(inScriptInstance, "custom scripts indicated pop could not transition to the Fighting state so pop is transitioning directly to Idle state");
	toReturn.Add("Idle");
	toReturn.Add(null);
	toReturn.Add(null);
	toReturn.Add(false);
	toReturn.Add(-1.0d);
	toReturn.Add(-1.0d);
	return toReturn;
}

//an enemy successfully fleeing the map will join existing fights to save comrades, but will not start fights on their own as their goal has been achieved.  So don't check for starting fights here.

//we're here because the pop is not close enough to a map edge to escape; keep moving
if(customScripts.runCustomScriptCheckTypes("attemptPathfindStateTransition", new List<object>() {"EnemyFleeingMapSuccess", inPop, inLocalMap, inBattleOngoing},
	out List<object> validTgt, true, typeof(bool), typeof(IFBuilding), typeof(IFPop), typeof(bool), typeof(double), typeof(double)))
{
	if((bool)validTgt[0])
	{
		addDebugMessage(inScriptInstance, "custom scripts indicated pop can and should stay in the EnemyFleeingMapSuccess state due to: " + " pop is not done moving");
		toReturn.Add("EnemyFleeingMapSuccess");
		toReturn.Add((IFBuilding)validTgt[1]);
		toReturn.Add((IFPop)validTgt[2]);
		toReturn.Add((bool)validTgt[3]);
		toReturn.Add((double)validTgt[4]);
		toReturn.Add((double)validTgt[5]);
		return toReturn;
	}
}				
//we failed to call a script correctly somehow.  Transition to Idle instead:
addDebugMessage(inScriptInstance, "custom scripts indicated pop could not stay in the EnemyFleeingMapSuccess state so pop is transitioning directly to Idle state");
toReturn.Add("Idle");
toReturn.Add(null);
toReturn.Add(null);
toReturn.Add(false);
toReturn.Add(-1.0d);
toReturn.Add(-1.0d);
return toReturn;
