i1.b:
//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_FleeingMapNeutral_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_FleeingMapNeutral_hasValidTargetForTransitionIn is checking if target exists for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//considerations:
//A) this pop must be neutral, during battle
bool neutral = (inPop.getCommunity() != getAPI().getLocalCommunity()) && !getAPI().isHostileCommunity(inPop.getCommunity());
bool first_consideration = neutral && inBattleOngoing;

toReturn.Add(first_consideration);

addDebugMessage(inScriptInstance, "return value=" + (bool)toReturn[0]);
return toReturn;

//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_FleeingMapNeutral_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_FleeingMapNeutral_findTargetForTransitionIn finding target for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//this is simple: just tell the pop to leave the map
toReturn.Add(null);
toReturn.Add(null);
toReturn.Add(true);
toReturn.Add(-1.0d);
toReturn.Add(-1.0d);
return toReturn;

i1.a:
//input count check:
if(inArguments.Length < 3)
{
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();
var customScripts = getAPI().getCustomScripts();

addDebugMessage(inScriptInstance, "default state_FleeingMapNeutral script is finding target for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//first: if we are close enough to the map edge, leave the map
double radiusToLeaveMap = customConsts.getDoubleConst("pathfinding_state_FleeingMapNeutral leave_map radius", out bool success);
if(!success) radiusToLeaveMap = 0.25d;
bool close_to_map_edge = false;
double x_loc = inPop.getMapLocationX();
double y_loc = inPop.getMapLocationY();
if((x_loc <= radiusToLeaveMap) || (y_loc <= radiusToLeaveMap))
{
	close_to_map_edge = true;
}
else if((inLocalMap.getMapDimensions().Item1 <= (x_loc + radiusToLeaveMap)) || (inLocalMap.getMapDimensions().Item2 <= (y_loc + radiusToLeaveMap)))
{
	close_to_map_edge = true;
}
if(close_to_map_edge)
{
	inPop.removePopFromMap(IFPop.ePopLocation.cUnspecifiedNowhere);
	addDebugMessage(inScriptInstance, "pop has escaped the battle; removing from map (pathfinding scripts will return information for Idle state)");
	toReturn.Add("Idle");
	toReturn.Add(null);
	toReturn.Add(null);
	toReturn.Add(false);
	toReturn.Add(-1.0d);
	toReturn.Add(-1.0d);
	return toReturn;
}

//if there is an enemy nearby, flee
if(customScripts.runCustomScriptCheckTypes("attemptPathfindStateTransition", new List<object>() {"FleeingHostile", inPop, inLocalMap, inBattleOngoing},
	out List<object> validTgt3, true, typeof(bool), typeof(IFBuilding), typeof(IFPop), typeof(bool), typeof(double), typeof(double)))
{
	if((bool)validTgt3[0])
	{
		addDebugMessage(inScriptInstance, "custom scripts indicated pop can and should transition to the FleeingHostile state due to: " + " there is an enemy nearby");
		toReturn.Add("FleeingHostile");
		toReturn.Add((IFBuilding)validTgt3[1]);
		toReturn.Add((IFPop)validTgt3[2]);
		toReturn.Add((bool)validTgt3[3]);
		toReturn.Add((double)validTgt3[4]);
		toReturn.Add((double)validTgt3[5]);
		return toReturn;
	}
}
else
{
	//we failed to call a script correctly somehow.  Transition to Idle instead:
	addDebugMessage(inScriptInstance, "custom scripts indicated pop could not transition to the FleeingHostile state so pop is transitioning directly to Idle state");
	toReturn.Add("Idle");
	toReturn.Add(null);
	toReturn.Add(null);
	toReturn.Add(false);
	toReturn.Add(-1.0d);
	toReturn.Add(-1.0d);
	return toReturn;
}

//we're here because there are no hostiles nearby, so stay in FleeingMapNeutral:
if(customScripts.runCustomScriptCheckTypes("attemptPathfindStateTransition", new List<object>() {"FleeingMapNeutral", inPop, inLocalMap, inBattleOngoing},
	out List<object> validTgt2, true, typeof(bool), typeof(IFBuilding), typeof(IFPop), typeof(bool), typeof(double), typeof(double)))
{
	if((bool)validTgt2[0])
	{
		addDebugMessage(inScriptInstance, "custom scripts indicated pop can and should stay in the FleeingMapNeutral state due to: " + " pop still wants to leave the map");
		toReturn.Add("FleeingMapNeutral");
		toReturn.Add((IFBuilding)validTgt2[1]);
		toReturn.Add((IFPop)validTgt2[2]);
		toReturn.Add((bool)validTgt2[3]);
		toReturn.Add((double)validTgt2[4]);
		toReturn.Add((double)validTgt2[5]);
		return toReturn;
	}
}
//we failed to call a script correctly somehow.  Transition to Idle instead:
addDebugMessage(inScriptInstance, "custom scripts indicated pop could not stay in the FleeingMapNeutral state so pop is transitioning directly to Idle state");
toReturn.Add("Idle");
toReturn.Add(null);
toReturn.Add(null);
toReturn.Add(false);
toReturn.Add(-1.0d);
toReturn.Add(-1.0d);
return toReturn;
