//input count and type check:
var customScripts = getAPI().getCustomScripts();
if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_Mustering_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_Mustering_hasValidTargetForTransitionIn is checking if target exists for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//considerations:
//A) the pop must be loyal, and must be unmustered
bool first_consideration = (inPop.getCommunity() == getAPI().getLocalCommunity()) && (!inPop.isMustered());

//B) the pop must have a mustering point, and must be within the radius of that mustering point:
double radius = customConsts.getDoubleConst("pathfinding_state_Mustering muster radius", out bool success);
if(!success) radius = 0.5d;
bool second_consideration = (inPop.hasMusteringPoint()) && (getAPI().calcDistanceBetweenPopAndBuildingOnMap(inPop, inPop.getMusteringPoint(), inLocalMap) < radius); 

toReturn.Add(first_consideration && second_consideration);

addDebugMessage(inScriptInstance, "return value=" + (bool)toReturn[0]);
return toReturn;

