if(!customScripts.runCustomScript("customScriptInputCheck", new List<object>() { inArguments, new List<Type>()
	{typeof(IFPop), typeof(IFMap), typeof(bool)}
  }, out _))
{
	throw new ArgumentException("pathfinding_state_Wounded_hasValidTargetForTransitionIn custom script received invalid arguments");
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();

addDebugMessage(inScriptInstance, "script pathfinding_state_Wounded_hasValidTargetForTransitionIn is checking if target exists for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

//two considerations: is the pop wounded, and is it not currently BeingAbducted
toReturn.Add(inPop.isWounded() && (inPop.getPathfindingState() != "beingabducted"));

addDebugMessage(inScriptInstance, "return value=" + (bool)toReturn[0]);
return toReturn;

