i1.a:
//input count check:
if(inArguments.Length < 3)
{
	return null;
}

IFPop inPop = (IFPop)inArguments[0];
IFMap inLocalMap = (IFMap)inArguments[1];
bool inBattleOngoing = (bool)inArguments[2];

var consts = getAPI().getConsts();
var customConsts = getAPI().getCustomConsts();
var variableHandler = getAPI().getVariableHandler();
var customScripts = getAPI().getCustomScripts();

addDebugMessage(inScriptInstance, "default state_Wounded script is finding target for " + inPop.getDebugName());
List<object> toReturn = new List<object>();

if(!inPop.isWounded())
{
	//if a pop is no longer wounded, allow it to Idle for a few moments, as if it is recovering.  It will then decide what to do.
	addDebugMessage(inScriptInstance, "pop is no longer wounded, so it will now transition directly to Idle state");
	toReturn.Add("Idle");
	toReturn.Add(null);
	toReturn.Add(null);
	toReturn.Add(false);
	toReturn.Add(-1.0d);
	toReturn.Add(-1.0d);
	return toReturn;
}

//A wounded pop remains wounded.  It cannot move.
toReturn.Add("Wounded");
toReturn.Add(null);
toReturn.Add(null);
toReturn.Add(false);
toReturn.Add(-1.0d);
toReturn.Add(-1.0d);
return toReturn;
